mapper.select:
select
c.id,pc.contract_number as "contractNumber", c.contract_id as "contractId" ,c.car_buy_financinger_id as "carBuyFinancingerId",cbf.name as "carBuyFinancingerName" ,c.brands_id as "brandsId",  c.series_id as "seriesId",  c.model_id as "modelId",  c.color_id as "colorId",
 c.card_frame_number as "cardFrameNumber",  c.engine_number as "engineNumber",  c.manufacture_time as "manufactureTime",  c.certificate_number as "certificateNumber",  c.invoice_number as "invoiceNumber",
 c.accessory_id as "accessoryId",  c.mortgage_type_id as "mortgageTypeId", mor.value as "mortgageTypeName",  c.buy_card_capital_type_id as "buyCardCapitalTypeId", buy.value as "buyCardCapitalTypeName",
 c.belonger_tpye as "belongerTpye",  c.belonger_company_header_id as "belongerCompanyHeaderId",ch.name as "belongerCompanyHeaderName" ,c.insurance_beneficiary_type as "insuranceBeneficiaryType",  c.beneficiary_company_header_id as "beneficiaryCompanyHeaderId",bch.name as "beneficiaryCompanyHeaderName",
 c.beneficia_financinger_id as "beneficiaFinancingerId",bf.name as "beneficiaFinancingerName" , c.plate_number as "plateNumber",  c.registration_number as "registrationNumber", c.registration_number_img as "registrationNumberImg",
 c.gps_supplier_id as "gpsSupplierId",gps.name as "gpsSupplierName",  c.price,  c.purchase_tax_rate as "purchaseTaxRate",  c.purchase_tax as "purchaseTax",  c.on_plate_cost as "onPlateCost",  c.gps_cost as "gpsCost",
 c.mortgage_cost as "mortgageCost",  c.vehicle_vessel_tax as "vehicleVesselTax",  c.other_cost as "otherCost",  c.storehouse_id as "storehouseId",s.name as "storehouseName",c.dealer_id as "dealerId",d.name as "dealerName",c.storehouse_status as "storehouseStatus",
 c.lease_status as "leaseStatus",  c.is_enable as "isEnable",  c.create_time as "createTime",  c.update_time as "updateTime",  c.create_by as "createBy",  c.update_by as "updateBy",  c.sort,  c.insurance_status as "insuranceStatus",
 c.gps_status as "gpsStatus",  c.plate_status as "plateStatus", c.mortgage_status as "mortgageStatus", c.in_storehouse_time as "inStorehouseTime",  c.sale_channel_type as "saleChannelType",  c.sale_channel_id as "saleChannelId",
 c.sale_time as "saleTime",  c.remarks, cb.name as "brandsName",cs.name as "seriesName",cm.business_model_name as "businessModelName",cc.name as "colorName",csr.name as "carSupplierName",c.invoicing_tax as "invoicingTax",invoiced_car_price as "invoicedCarPrice",
c.branch_company_id as "branchCompanyId",bc.name as "branchCompanyName",c.storehouse_type
from lease_car c
left join lease_car_buy_financinger cbf on c.car_buy_financinger_id=cbf.id
left join lease_car_brands cb on c.brands_id=cb.id
left join lease_car_series cs on c.series_id=cs.id
left join lease_car_model cm on c.model_id=cm.id
left join lease_car_color cc on c.color_id=cc.id
left join  lease_company_header ch on c.belonger_company_header_id=ch.id
left join  lease_company_header bch on c.beneficiary_company_header_id=bch.id
left join lease_car_buy_financinger bf on c.beneficia_financinger_id=bf.id
left join lease_purchase_contract pc on c.contract_id=pc.id
left join lease_gps_supplier gps on c.gps_supplier_id=gps.id
left join lease_car_supplier csr on pc.car_supplier_id=csr.id
left join lease_branch_company bc on c.branch_company_id =bc.id
left join lease_dict buy on c.buy_card_capital_type_id=buy.id
left join lease_dict mor on c.mortgage_type_id=mor.id
left join lease_storehouse s on c.storehouse_id=s.id
left join lease_dealer d on c.dealer_id=d.id
where 1=1

  and  (c.plate_number like concat('%',#{number},'%') or c.engine_number like concat('%',#{number},'%') or c.card_frame_number like concat('%',#{number},'%'))


  and  c.storehouse_status = #{storehouseStatus}


  and   c.storehouse_id = #{storehouseId}


  and   c.dealer_id = #{dealerId}


  and   c.lease_status = #{leaseStatus}


  and  c.mortgage_type_id = #{mortgageTypeId}


  and  c.buy_card_capital_type_id = #{buyCardCapitalTypeId}


  and  c.insurance_status = #{lnsuranceStatus}


  and  c.gps_status = #{gpsStatus}


  and  c.plate_status = #{plateStatus}


  and  c.mortgage_status = #{mortgageStatus}


  and  csr.id = #{carSupplierId}


  and  c.brands_id = #{brandsId}


and  c.series_id = #{seriesId}


  and  c.model_id = #{modelId}


  and  c.color_id = #{colorId}


  and  c.branch_company_id = #{branchCompanyId}

ORDER BY c.create_time DESC

select ca.id,ca.brands_id as "brandsId", ca.series_id as "seriesId", ca.model_id as "modelId", ca.color_id as "colorId", ca.card_frame_number as "cardFrameNumber", ca.engine_number as "engineNumber",ca.plate_number as "plateNumber", cb.name as "brandsName",cs.name as "seriesName",cm.business_model_name as "businessModelName",cc.name as "colorName",cmc.price, case when so.id is not null then true else false end as "used",so.id as "carSchemeOrderId" from lease_car ca join lease_car_brands cb on ca.brands_id=cb.id join lease_car_series cs on ca.series_id=cs.id join lease_car_model cm on ca.model_id=cm.id join lease_car_color cc on ca.color_id=cc.id join lease_car_model_color cmc on ca.model_id=cmc.car_model_id and ca.color_id=cmc.car_color_id and ca.branch_company_id=cmc.branch_company_id left join lease_scheme_order so on so.card_id=ca.id where 1=1
and (ca.plate_number like concat('%',#{number},'%') or ca.engine_number like concat('%',#{number},'%') or ca.card_frame_number like concat('%',#{number},'%'))
and cmc.branch_company_id = #{branchCompanyId}

select

from lease_car
WHERE 1=1

  and plate_number = #{plateNumber,jdbcType=VARCHAR}


  and engine_number = #{engineNumber,jdbcType=VARCHAR}



  and card_frame_number = #{cardFrameNumber,jdbcType=VARCHAR}



  and id != #{id,jdbcType=BIGINT}
